import React, { useState, useMemo } from 'react';

const RootConnectorExercise = ({ currentLesson }) => {
  // Encontra a primeira raiz válida para o exercício, ignorando as nulas.
  const targetRoot = useMemo(() => {
    if (!currentLesson || !currentLesson.palavras) return null;
    // Filtra as palavras para encontrar a primeira que tenha uma raiz válida.
    const firstWordWithRoot = currentLesson.palavras.find(p => p.raiz_shoresh);
    return firstWordWithRoot ? firstWordWithRoot.raiz_shoresh : null;
  }, [currentLesson]);

  // Estado para rastrear o status de cada palavra (default, correct, incorrect)
  const [wordStatuses, setWordStatuses] = useState({});

  const handleWordClick = (palavra) => {
    const status = palavra.raiz_shoresh === targetRoot ? 'correct' : 'incorrect';
    setWordStatuses(prevStatuses => ({
      ...prevStatuses,
      [palavra.palavra_hebraica]: status
    }));
  };

  if (!targetRoot) {
    return <p>Não foi possível carregar o exercício.</p>;
  }

  return (
    <div style={{ padding: '20px', border: '1px solid #eee', borderRadius: '8px' }}>
      <h3 style={{ textAlign: 'center', marginBottom: '25px' }}>Exercício: Conecte as Palavras</h3>
      <p style={{ textAlign: 'center', fontSize: '1.2em' }}>
        Clique nas palavras que pertencem à raiz: <strong style={{ fontSize: '1.5em', color: '#005a9e' }}>{targetRoot}</strong>
      </p>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(100px, 1fr))', gap: '15px', marginTop: '20px' }}>
        {currentLesson.palavras.map((palavra, index) => {
          const status = wordStatuses[palavra.palavra_hebraica] || 'default';
          let backgroundColor = '#f0f0f0'; // Cor padrão
          if (status === 'correct') {
            backgroundColor = '#d4edda'; // Verde para correto
          } else if (status === 'incorrect') {
            backgroundColor = '#f8d7da'; // Vermelho para incorreto
          }

          return (
            <button
              key={index}
              onClick={() => handleWordClick(palavra)}
              style={{
                padding: '15px',
                border: 'none',
                borderRadius: '5px',
                backgroundColor: backgroundColor,
                cursor: 'pointer',
                fontSize: '1.1em',
                fontWeight: 'bold',
                transition: 'background-color 0.3s ease'
              }}
            >
              {palavra.palavra_hebraica}
            </button>
          );
        })}
      </div>
    </div>
  );
};

export default RootConnectorExercise;
